INSERT INTO `t_catg_ques_ans` VALUES (1,13,13,'<div><!--block-->A Maven repository is a location where all the plugins, project jars, library jars or other particular project related artifacts are stored and used then&nbsp; by Maven.<br><br></div><div><!--block-->?<span data-trix-cursor-target=\"true\" data-trix-serialize=\"false\">﻿</span><figure class=\"attachment attachment-preview\" data-trix-attachment=\"{&quot;contentType&quot;:&quot;image&quot;,&quot;height&quot;:479,&quot;url&quot;:&quot;http://www.wideskills.com/sites/default/files/subjects/Maven%20Tutorial/12/m74.png&quot;,&quot;width&quot;:768}\" data-trix-content-type=\"image\" data-trix-id=\"46717\" contenteditable=\"false\"><img src=\"http://www.wideskills.com/sites/default/files/subjects/Maven%20Tutorial/12/m74.png\" data-trix-mutable=\"true\" data-trix-store-key=\"imageElement/46717/46718/http://www.wideskills.com/sites/default/files/subjects/Maven%20Tutorial/12/m74.png\" width=\"768\" height=\"479\"><figcaption class=\"caption\"></figcaption></figure><span data-trix-cursor-target=\"true\" data-trix-serialize=\"false\">﻿</span>?</div>','2017-10-18 20:00:57','2017-10-18 20:00:57',1,'2018-08-16 16:13:02');
