INSERT INTO `t_catg_ques_ans` VALUES (1,26,15,'<div><!--block--><strong>Good effect dene wali chijein</strong></div><ul><li><!--block-->Laung/Clove<ul><li><!--block-->Isko khakar, burai bahar nikalti hai</li></ul></li><li><!--block-->Bhawan ka bhajan<ul><li><!--block-->Karodo log isko kar ke man me shanti feel karte hain.</li></ul></li><li><!--block-->Chai<ul><li><!--block-->Isme nicotine bhi hai, par ye hum socially pi sakte hain. Isko pi ke bhi mann shant ho jata hai.</li></ul></li><li><!--block--><strong>Anulom-Vilom</strong><ul><li><!--block-->isse mann shant hota hai.</li></ul></li><li><!--block-->Maa Papa se baat<ul><li><!--block-->Maa Papa se baat karke acha lagta hai.&nbsp;<ul><li><!--block-->Baat karne ke dauran positive baatein sochna acha lagta hai.</li></ul></li></ul></li><li><!--block-->Hanuman chalisa paath</li><li><!--block-->Hanuman chalisa mantra ka manan<ul><li><!--block-->Sankat se Hanuman Churawe, man karm vachan dhyan jo lawe.</li><li><!--block-->Sankat kate mite sab pira, jo sumire hanumat balweera.</li></ul></li><li><!--block-->Koi acchi purani event par discussion.</li><li><!--block-->Good future plan.</li></ul>','2018-01-18 04:42:30','2018-02-12 04:51:30',10,'2018-08-16 16:13:02');
