INSERT INTO `t_catg_ques_ans` VALUES (1,26,38,'<div><!--block-->A spoofing attack is when a malicious party impersonates another device or user on a network so as to launch attacks against network hosts, steal data, unfold malware or bypass access controls. Different Spoofing attacks are deployed by malicious parties to achieve this.</div>','2018-11-17 22:48:31','2018-11-17 22:48:31',1,'2018-11-17 22:48:31');
