</head>
<body>
    <div id="formContainer" class="form-container"></div>

    <script>
        const formConfig = {
            elements: [
                {
                    type: 'input',
                    attributes: {
                        type: 'text',
                        placeholder: 'Name',
                        name: 'name'
                    },
                    style: {
                        padding: '10px',
                        fontSize: '16px',
                        border: '1px solid #ccc',
                        borderRadius: '3px'
                    },
                    events: {
                        change: (event) => {
                            console.log('Name changed:', event.target.value);
