                        borderRadius: '3px'
                    },
                    events: {
                        change: (event) => {
                            console.log('Email changed:', event.target.value);
                        }
                    }
                },
                {
                    type: 'select',
                    attributes: {
                        name: 'options'
                    },
                    options: [
                        { value: '', text: 'Select an option' },
                        { value: 'option1', text: 'Option 1' },
                        { value: 'option2', text: 'Option 2' }
                    ],
                    style: {
                        padding: '10px',
                        fontSize: '16px',
                        border: '1px solid #ccc',
                        borderRadius: '3px'
                    },
                    events: {
                        change: (event) => {
                            console.log('Option selected:', event.target.value);
                        }
                    }
                },
                {
                    type: 'label',
                    textContent: 'Gender:',
                    style: {}
                },
                {
                    type: 'input',
                    attributes: {
                        type: 'radio',
                        name: 'gender',
                        value: 'male'
                    },
                    label: 'Male',
                    style: {},
                    events: {
                        change: (event) => {
                            console.log('Gender selected:', event.target.value);
                        }
                    }
                },
                {
                    type: 'input',
                    attributes: {
                        type: 'radio',
                        name: 'gender',
                        value: 'female'
                    },
                    label: 'Female',
                    style: {},
                    events: {
                        change: (event) => {
                            console.log('Gender selected:', event.target.value);
                        }
                    }
                },
                {
