                List<Question> questions = fetchQuestionsFromSource(sourceConn, offset, BATCH_SIZE);
                hasMoreData = !questions.isEmpty();

                if (hasMoreData) {
                    // Step 2: Process records in parallel
                    List<Callable<Void>> tasks = new ArrayList<>();
                    for (Question question : questions) {
