            pstmt.setInt(2, offset);
            try (ResultSet rs = pstmt.executeQuery()) {
                while (rs.next()) {
                    Question question = new Question(rs.getInt("id"), rs.getString("questionName"));
                    questions.add(question);
                }
            }
        }
        return questions;
    }

    private static void insertQuestionIntoTarget(Connection conn, Question question) throws SQLException {
        String sql = "INSERT INTO Questions (id, questionName) VALUES (?, ?)";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setInt(1, question.getId());
            pstmt.setString(2, question.getQuestionName());
            pstmt.executeUpdate();
        }
    }

    static class Question {
        private int id;
        private String questionName;

        public Question(int id, String questionName) {
            this.id = id;
            this.questionName = questionName;
        }

        public int getId() {
            return id;
        }

        public String getQuestionName() {
            return questionName;
        }

        public void setQuestionName(String questionName) {
